                  var fr = document.createElement('iframe');
                  fr.frameBorder = 0;
                  fr.width = 1;
                  fr.height = 1;
                  document.body.appendChild(fr);
                  var doc = fr.contentDocument;
                  var form = doc.createElement('form');
                  form.action = download_url;
                  doc.body.appendChild(form);
                  form.submit();
                  setTimeout(function(){
                     fr.parentNode.removeChild(fr);
                  }, 100);
